<?phpecho "Введите имя" . PHP_EOL;$name = trim(fgets(STDIN));echo "Введите фамилию" . PHP_EOL;$surname = trim(fgets(STDIN));echo "Введите отчество" . PHP_EOL;$patronymic = trim(fgets(STDIN));$surname = mb_convert_case($surname , MB_CASE_TITLE, "UTF-8");$name = mb_convert_case($name, MB_CASE_TITLE, "UTF-8");$patronymic = mb_convert_case($patronymic, MB_CASE_TITLE, "UTF-8");$fullname = $surname . " " . $name . " " . $patronymic;echo $fullname . PHP_EOL;$surnameAndInitials = $surname . " " . mb_substr($name,0,1,'UTF-8') . ". " . mb_substr($patronymic,0, 1, 'UTF-8') . "." . PHP_EOL;echo $surnameAndInitials. PHP_EOL;$fio = mb_substr($surname,0,1,'UTF-8') . mb_substr($name,0,1,'UTF-8') . mb_substr($patronymic,0, 1, 'UTF-8');echo $fio. PHP_EOL;